    public function getSourceContext()
    {
        return new Source("{% extends 'site/layout.html.twig' %}

{% block title %}Connexion{% endblock %}

{% block content %}

{% include 'utils/_flash.html.twig' %}

<form method=\"post\">
    {% if error %}
        <div class=\"alert alert-danger\">{{ error.messageKey|trans(error.messageData, 'security') }}</div>
    {% endif %}

    <h1 class=\"h3 mb-3 font-weight-normal\">Connexion</h1>
    <div class=\"form-group\">
        <label for=\"inputEmail\">Email</label>
        <input type=\"email\" value=\"{{ last_username }}\" name=\"email\" id=\"inputEmail\" class=\"form-control\" required autofocus>
    </div>

    <div class=\"form-group\">
        <label for=\"inputPassword\">Mot de passe</label>
        <input type=\"password\" name=\"password\" id=\"inputPassword\" class=\"form-control\" required>
    </div>

    <input type=\"hidden\" name=\"_csrf_token\" value=\"{{ csrf_token('authenticate') }}\">

    {#
        Uncomment this section and add a remember_me option below your firewall to activate remember me functionality.
        See https://symfony.com/doc/current/security/remember_me.html
    #}

    <div class=\"checkbox mb-3\">
        <label>
            <input type=\"checkbox\" name=\"_remember_me\"> Se souvenir de moi ?
        </label>
    </div>


    <button class=\"btn btn-lg btn-primary\" type=\"submit\">Se connecter</button>
</form>
{% endblock %}
", "security/login.html.twig", "/Users/arnaudbassey/Documents/DWWM/Initiation symphony/dwm-blog-19/templates/security/login.html.twig");
