    public function getSourceContext()
    {
        return new Source("{% extends 'site/blog/layout.html.twig' %}

{% block title %}
    {{ article.title }}
{% endblock %}

{% block blogContent %}

    {% if article.image is not null %}
        <img style=\"width: 100%;\" src=\"{{ asset(article.image) }}\" alt=\"{{ article.title }}\">
    {% endif %}

    <h1>{{ article.title }}</h1>
    <p>{{ article.publishedAt | date('d-m-Y H:i') }} - <a href=\"{{ path('site_blog_category', {'slug' : article.category.slug}) }}\" class=\"badge badge-primary\">{{ article.category.name }}</a></p>

    <p>{{ article.content }}</p>

{% endblock %}


