    public function getSourceContext()
    {
        return new Source("{% extends 'admin/layout.html.twig' %}

{% block title %}Liste des catégories{% endblock %}

{% block content %}
    <h1>Liste des catégories</h1>

    <p>
        <a href=\"{{ path('category_new') }}\" class=\"btn btn-primary\">Ajouter une catégorie</a>
    </p>

    <table class=\"table\">
        <thead>
            <tr>
                <th>Name</th>
                <th>Slug</th>
                <th>Action</th>
            </tr>
        </thead>
        <tbody>
        {% for category in categories %}
            <tr>
                <td>{{ category.name }}</td>
                <td>{{ category.slug }}</td>
                <td>
                    <a href=\"{{ path('category_edit', {'id': category.id}) }}\">Modifier</a>
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"3\">Aucun résultat</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>

    {{ knp_pagination_render(categories) }}

{% endblock %}
", "admin/category/index.html.twig", "/Users/arnaudbassey/Documents/DWWM/Initiation symphony/dwm-blog-19/templates/admin/category/index.html.twig");
